import React from 'react';
import Navbar from '../../components/Navbar';
import $ from 'jquery';
import { useEffect} from "react";
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import '../../css/App.css';
import colorspectrum from '../../images/Spectrum.png';
import exampleImage from '../../images/Cat.png';
import GetUserId from '../../utils/GetUserId';


const Survey = () => {

    var index = 1;
    const userId = GetUserId();

    useEffect(() => {
        console.log(userId)
        createPixelMask();
      },[])

    function checkUserData() {
        // Will call express server with userID and then either create pixel mask or increment index by 1 and call express server again
    }  

    function createPixelMask(){
        var table = document.getElementById("pixel_Table");
        var row;
        var cell;
        for(var i=0;i<8;i++){
            row = table.insertRow(i);
              for(var j=0;j<8;j++){
                cell = row.insertCell(j);
                cell.addEventListener('click', IncreaseOpacity);
                cell.addEventListener('contextmenu', DecreaseOpacity)
            }
        }

        document.getElementById("pixel_Table").style.backgroundImage = 'url(' + exampleImage + ')';
    }
    
    function DecreaseOpacity(event) {
		event.preventDefault()
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level > "0.0") {
			opacity_level = opacity_level - 0.15;
			$(event.target).css("opacity", opacity_level);
		}
	}
		
	function IncreaseOpacity(event) {
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level < "0.9") {
			opacity_level = opacity_level + 0.15;
			$(event.target).css("opacity", opacity_level);
		}	
	}

        function saveData() {
            var table = document.getElementById('pixel_Table')
            var tableData = "";
            for (var i = 0, row; row = table.rows[i]; i++) {
                for (var j = 0, col; col = row.cells[j]; j++) {
                    tableData += "," + $(col).css("opacity");
                    console.log('Row ' + i + ' Column ' + j + ' Value ' + $(col).css("opacity"));
                }
            }
            axios.post('https://sheetdb.io/api/v1/2mrwhvfjdfnvm',{
                "data": {"Profile ID": "1", "Image": "Cat.png", "Opacity Matrix": tableData}
            }).then( response => {
                console.log(response.data);
            });
        }	

return (
    <div className="survey">
        <Navbar/>
        <div style={{height: "40vw", position: "relative"}}>
            <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", textAlign: "center"}}>
                <h1>Image {index}/10 </h1>
                    <table id="pixel_Table"></table>
                    <div>
                        <Button style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary" >Previous Image</Button>
                        <Button style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary">Next Image</Button>       
                    </div>
                    <figure>
                    <img src={colorspectrum} alt="Spectrum" />
                        <figcaption><h5>Degree of importance (dark green indicates high importance)</h5></figcaption>
                    </figure>
            </div> 
        </div>          
    </div>
    )   
}


export default Survey;
