import React from 'react';
import Navbar from '../../components/Navbar';
import Button from 'react-bootstrap/Button';
import $ from 'jquery';
import '../../css/App.css';
import { useEffect } from 'react';
import colorspectrum from '../../images/Spectrum.png';
import exampleImage from '../../images/Cat.png';
import { useNavigate } from 'react-router-dom';

const Home = () => {
	const SPREADSHEET_ID = process.env.REACT_APP_SPREADSHEET_ID;
    const CLIENT_EMAIL = process.env.REACT_APP_GOOGLE_CLIENT_EMAIL;
    const PRIVATE_KEY = process.env.REACT_APP_GOOGLE_SERVICE_PRIVATE_KEY;

    const spreadsheet = new GoogleSpreadsheet(SPREADSHEET_ID);

    const appendSpreadsheet = async (row) => {
        try {
            await spreadsheet.useServiceAccountAuth({
                client_email: CLIENT_EMAIL,
                private_key: PRIVATE_KEY,
            });
        // loads document properties and worksheets
        await spreadsheet.loadInfo();
        const sheet = spreadsheet.sheetsById["Test"];
        const result = await sheet.addRow(row);
        return result;
        } catch (e) {
            console.error('Error: ', e);
        }
    };
    const newRow = { Name: "new name", Value: "new value" };
    

	useEffect(() => {
		appendSpreadsheet(newRow);
		createPixelMask();
	}, [])

	let navigate = useNavigate();

	function createPixelMask(){
		var table = document.getElementById("pixel_Table");
		var row;
		var cell;
    	for(var i=0;i<5;i++){
			row = table.insertRow(i);
      		for(var j=0;j<5;j++){
			    cell = row.insertCell(j);
			    cell.addEventListener('click', clickHandlerColumn);
				cell.addEventListener('contextmenu', resetOpacity)
        	}
    	}

    	document.getElementById("pixel_Table").style.backgroundImage = 'url(' + exampleImage + ')';
    }
		
	function resetOpacity(event) {
		event.preventDefault()
		$(event.target).css("opacity", ".0");
	}
		
	function clickHandlerColumn(event) {
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level == "0.9") {
			$(event.target).css("opacity", ".0");
		}
		else {
			opacity_level = opacity_level + 0.15;
			$(event.target).css("opacity", opacity_level);
			}		
		}

    return (
        <div className="home">		
            <Navbar/>
            <div style={{height: "40vw", position: "relative"}}>
                <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", textAlign: "center"}}>
					<h1>Example Image</h1>
					<h4>Left click on a pixel to mark it's importance. <br></br> You can right click to reset the pixel's opacity back to 0%</h4>
						<table id="pixel_Table"></table>
						<figure>
						<img src={colorspectrum} alt="Spectrum" />
							<figcaption><h5>Degree of importance (dark green indicates high importance)</h5></figcaption>
						</figure>
						<br></br>
						<br></br>
						<Button variant="outline-secondary" onClick={() => {
							navigate('/survey');
						}}
						>Start Survey</Button> 
                </div> 
            </div>          
        </div>
    )
}

export default Home;
