import colorspectrum from '../images/resized/Spectrum.png';
import Create3DArray from '../utils/Create3DArray';
import GetImagePath from '../utils/GetImagePath';
import { useNavigate } from 'react-router-dom';
import GetUserId from '../utils/GetUserId';
import $ from 'jquery';
import { useEffect } from "react";
import Button from 'react-bootstrap/Button';

const SurveyPart1 = () => {
    let index = 1;
    let navigate = useNavigate();
    var userData = undefined;
    const userId = GetUserId();
    

    useEffect(() => {
        if(sessionStorage.getItem('data') == null) {
            userData = Create3DArray(5,8,8);
            window.sessionStorage.setItem('data', JSON.stringify(userData));
        }
        else {
            userData = JSON.parse(window.sessionStorage.getItem('data'));
        }
        createPixelMask();
        SetImageValues()        
      },[])
    
    function SetImageValues() {
        var table = document.getElementById('pixel_Table')
        var row;
        for (var i = 0, row; row = table.rows[i]; i++) {
            for (var j = 0, col; col = row.cells[j]; j++) {
                if(userData[index-1][i][j] != null) {
                    $(col).css("opacity", userData[index-1][i][j]);
                }
                else {
                    $(col).css("opacity", "0.0");
                }
            }
        }
        document.getElementById("pixel_Table").style.backgroundImage = 'url(' + GetImagePath(index-1) + ')';
    }

    function createPixelMask(){

        var table = document.getElementById("pixel_Table");
        var row;
        var cell;
        for(var i=0;i<8;i++){
            row = table.insertRow(i);
              for(var j=0;j<8;j++){
                cell = row.insertCell(j);
                cell.addEventListener('click', IncreaseOpacity);
                cell.addEventListener('contextmenu', DecreaseOpacity)
            }
        }
    }
    
    function DecreaseOpacity(event) {
		event.preventDefault()
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level > "0.0") {
			opacity_level = opacity_level - 0.30;
			$(event.target).css("opacity", opacity_level);
		}
	}
		
	function IncreaseOpacity(event) {
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level < "0.9") {
			opacity_level = opacity_level + 0.30;
			$(event.target).css("opacity", opacity_level);
		}	
	}

    function NextImage() {
        saveData();
        if(index == 5) {
            document.getElementById("SurveyPart1").style.display = "none";
            document.getElementById("SurveyPart2").style.display = "inline";
            console.log("Testing webserver");
            submitData();
        }
        else {     
        index = index + 1;
        var imageCount = index;
        document.getElementById("headline").innerHTML = "Bild " + imageCount + " av 5";
        if(index == 5) {
            
        }
        SetImageValues();
        }
    }

    function PreviousImage() {
        saveData();
        if(index == 1) {
            navigate('/home')
        }
        else {         
            index = index - 1;
            var imageCount = index;
            document.getElementById("headline").innerHTML = "Bild " + imageCount + " av 5";
            if(index < 5) {
                document.getElementById("Next-button").innerHTML = "N채sta";
            }
            SetImageValues();
        }
        }

    function saveData() {
        var table = document.getElementById('pixel_Table')
    		for (var i = 0, row; row = table.rows[i]; i++) {
        		for (var j = 0, col; col = row.cells[j]; j++) {
                    userData[index-1][i][j] = $(col).css("opacity");
                }
            } 
        window.sessionStorage.setItem('data', JSON.stringify(userData));         
    }

    function submitData() {
        const data = {
            userId: userId,
            userData: window.sessionStorage.getItem('data')
        }       

        fetch("https://stormy-plains-42247.herokuapp.com/submit", {
            method: "POST",
            headers: {
                'Content-type': "application/json"
            },
            body: JSON.stringify(data)
        })
    }


    return (
        <div id="SurveyPart1" style={{display: "none"}}>
            <h1 id="headline">Bild 1 av 5</h1>
                    <table id="pixel_Table"></table>
                    <div>
                        <Button style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary" onClick={PreviousImage}>Tillbaka</Button>
                        <Button id="Next-button" style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary"onClick={NextImage}>N채sta</Button>       
                    </div>
                    <figure>
                    <img src={colorspectrum} alt="Spektrum" />
                        <figcaption><h5>Gradskala</h5></figcaption>
                    </figure>
        </div>
    )
}

export default SurveyPart1;
