import Home from './pages/protected/Home';
import Survey from './pages/protected/Survey';
import Login from './pages/auth/Login';
import './css/App.css';
import {BrowserRouter as Router, Routes, Route } from 'react-router-dom'
import ProtectedRoutes from './utils/ProtectedRoutes';
import { useEffect } from 'react';
import { auth } from './configs/firebaseConfig';
import { onAuthStateChanged } from "firebase/auth";
import { useSelector, useDispatch } from "react-redux";
import { saveUser} from "./redux/slice/authSlice";


function App() {
  const user = useSelector((state) => state.auth.value);
  const dispatch = useDispatch();
  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        dispatch(saveUser(user.uid));
      } else {
        dispatch(saveUser(undefined));
        sessionStorage.removeItem('data', undefined);
      }
    });
  }, [auth, dispatch]);


  return (
    <Router>
    <div className="app">
      <Routes>         
        <Route path="/" element={user ? <Home /> : <Login/>} replace/>
        <Route path="/home" replace element={<ProtectedRoutes/>}>
        <Route path="/home" replace element={<Home/>}/>
        </Route>
        <Route path="/survey" replace element={<ProtectedRoutes/>}>
        <Route path="/survey" replace element={<Survey/>}/>
        </Route>
      </Routes>
    </div>
    </Router>
  );
}

export default App;
