import React from 'react';
import Navbar from './NavbarHome';
import '../css/App.css';
import { CSSTransition } from 'react-transition-group';
import { TransitionGroup } from 'react-transition-group'; 

const Home = ({ user }) => {

    
    this.state= {
        greetings : ['Welcome to our Survey ', 'on', 'Human Image Reasoning']
    }
    


    return (
        <div className="home">
            <Navbar user={user}/>
            <div style={{height: "40vw", position: "relative"}}>
                <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", transform: "translate(-50%, -50%)"}}>
                    <TransitionGroup>
                        { this.state.greetings.map((greeting, index) => (
                        <CSSTransition
                            key={'csst' + index}
                            in={true}
                            appear={true}
                            timeout={2500}
                            
                            onEnter = { ()=> console.log('onEnter')}
                            onEntering = { ()=> console.log('onEntering')}
                            onEntered = { ()=> console.log('onEntered')}

                            onEntered = { ()=>this.setState({greeting})}                          

                            onExit = { ()=> console.log('onExit')}
                            onExiting = { ()=> console.log('onExiting')}
                            onExited = { ()=> console.log('onExited')}

                            classNames="example">
                            <h1 style={{textAlign: "center"}} key={index}>{greeting}</h1>    
                        </CSSTransition>
                        ))}
                    </TransitionGroup>
                </div> 
            </div>          
        </div>
    )
}

export default Home;
