import React from 'react';
import Navbar from '../../components/Navbar';
import $ from 'jquery';
import { useEffect} from "react";
import Button from 'react-bootstrap/Button';
import '../../css/App.css';
import colorspectrum from '../../images/Spectrum.png';
import CreateArray from '../../utils/CreateArray';
import GetImagePath from '../../utils/GetImagePath';

const Survey = () => {
    var index = 0;
    const userData = CreateArray(5,5,5);

    useEffect(() => {
        createPixelMask();
        SetImageValues() 
      },[])
    
    function SetImageValues() {
        var table = document.getElementById('pixel_Table')
        var row;
        for (var i = 0, row; row = table.rows[i]; i++) {
            for (var j = 0, col; col = row.cells[j]; j++) {
                if(userData[index][i][j] != null) {
                    //$(col).css("opacity", userData[index][i][j]);
                }
            }
        }
        document.getElementById("pixel_Table").style.backgroundImage = 'url(' + GetImagePath(index) + ')';
    }

    function createPixelMask(){

        var table = document.getElementById("pixel_Table");
        var row;
        var cell;
        for(var i=0;i<5;i++){
            row = table.insertRow(i);
              for(var j=0;j<5;j++){
                cell = row.insertCell(j);
                cell.addEventListener('click', IncreaseOpacity);
                cell.addEventListener('contextmenu', DecreaseOpacity)
            }
        }
    }
    
    function DecreaseOpacity(event) {
		event.preventDefault()
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level > "0.0") {
			opacity_level = opacity_level - 0.15;
			$(event.target).css("opacity", opacity_level);
		}
	}
		
	function IncreaseOpacity(event) {
		var opacity_level = parseFloat($(event.target).css("opacity"));
		if (opacity_level < "0.9") {
			opacity_level = opacity_level + 0.15;
			$(event.target).css("opacity", opacity_level);
		}	
	}

    function NextImage() {
        saveData();
        index = index + 1;
        SetImageValues();
    }

    function PreviousImage() {
        saveData();
        index = index - 1;
        SetImageValues();
        }

    function saveData() {
        var table = document.getElementById('pixel_Table')
    		for (var i = 0, row; row = table.rows[i]; i++) {
        		for (var j = 0, col; col = row.cells[j]; j++) {
                    userData[index][i][j] = $(col).css("opacity");
                }
            }         
    }	

return (
    <div className="survey">
        <Navbar/>
        <div style={{height: "40vw", position: "relative"}}>
            <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", textAlign: "center"}}>
                <h1>Image {(index+1)}/5 </h1>
                    <table id="pixel_Table"></table>
                    <div>
                        <Button style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary" onClick={PreviousImage}>Back</Button>
                        {(index == 5) ? <Button style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary" onClick={NextImage}>Finish</Button> : 
                        <Button style={{ marginBottom: "20px", marginRight: "20px", marginLeft: "20px"}} variant="outline-secondary"onClick={NextImage}>Next</Button>}       
                    </div>
                    <figure>
                    <img src={colorspectrum} alt="Spectrum" />
                        <figcaption><h5>Degree of importance (dark green indicates high importance)</h5></figcaption>
                    </figure>
            </div> 
        </div>          
    </div>
    )   
}



export default Survey;
