import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import '../css/fade.css';

const Fader = ({text}) => {


    const [fadeProp, setFadeProp] = useState({
        fade: 'fade-out',
    })
    var isRunning = true;
    let timeout;
    useEffect(() => {
        if(isRunning) {
            timeout = setInterval(() => {
                if(fadeProp.fade === 'fade-in') {
                    setFadeProp({
                        fade: 'fade-out'
                    })
                    isRunning = false;
                }
                else {
                    setFadeProp( {
                        fade: 'fade-in'
                    })       
                }
            }, 4000);
        }
        return () => clearInterval(timeout)
    }, [isRunning])

    return (
        <>
        <h1 style={{textAlign: "center"}} className={fadeProp.fade}>{text}</h1>
        </>
    )
}

Fader.propTypes = {
    text: PropTypes.string,
}

export default Fader;
