import React from 'react';
import Navbar from './NavbarHome';
import axios from 'axios';
import '../css/App.css';

const Home = ({ user }) => {
    const images = ["images\Cat.jpg", "images/Penguin.jpg"];

		function createPixelMask(){
			var table = document.getElementById("pixel_Table");
			var row;
			var cell;
    		for(var i=0;i<5;i++){
			    row = table.insertRow(i);
      			for(var j=0;j<5;j++){
			        cell = row.insertCell(j);
			        cell.addEventListener('click', clickHandlerColumn);
        		}
    		}

    		document.getElementById("pixel_Table").style.backgroundImage = "url(images[index])";
    	}
		
		function clickHandlerColumn(event) {
			var opacity_level = parseFloat(document.getElementById(event.target).style.opacity);
			if (opacity_level == "0.75") {
				document.getElementById(event.target).style.opacity = 0.0;
			}
			else {
				opacity_level = opacity_level + 0.25;
				document.getElementById(event.target).style.opacity = opacity_level;
				}		
			}

			function saveData() {
				var table = document.getElementById('pixel_Table')
				var tableData = "";
    			for (var i = 0, row; row = table.rows[i]; i++) {
        			for (var j = 0, col; col = row.cells[j]; j++) {
        				tableData += "," + document.getElementByIdcol.style.opacity;
        				console.log('Row ' + i + ' Column ' + j + ' Value ' + document.getElementByIdcol.style.opacity);
        			}
        		}
        		axios.post('https://sheetdb.io/api/v1/2mrwhvfjdfnvm',{
        			"data": {"Profile ID": "1", "Image": "Cat.jpg", "Opacity Matrix": tableData}
    			}).then( response => {
        			console.log(response.data);
    			});
			}	
    


    return (
        <div className="home">
            <Navbar user={user}/>
            <div style={{height: "40vw", position: "relative"}}>
                <div style={{position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", transform: "translate(-50%, -50%)"}}>
					<h1>Image 1/10</h1>
					<h4>Click on the pixels to mark how important <br></br> you deem the pixels to be to identify the object in the image</h4>
						<table id="pixel_Table"></table>
						<button id="save_Data" onclick="saveData();">Submit Answer</button>
						<figure>
						<img src={require('xai-survey-app\src\images\resources\Spectrum.png')} />
							<figcaption>Degree of importance (dark green indicates high importance)</figcaption>
						</figure>
                </div> 
            </div>          
        </div>
    )
}

export default Home;
